import UIKit

protocol FormBusinessLogic{
    func fetchCells(request: FormModels.FetchCell.Request)
}

protocol FormDataStore{
    var cells: [Cell]? { get }
}

class FormInteractor:FormBusinessLogic, FormDataStore{
    var presenter: FormPresentationLogic?
    var worker: FormWorker?
    var cells: [Cell]?
    
    // MARK: Fetch Cells
    
    func fetchCells(request: FormModels.FetchCell.Request){
        worker = FormWorker()
        worker?.fetchCells { (cells) -> Void in
            self.cells = cells
            let response = FormModels.FetchCell.Response(cells: cells)
            self.presenter?.presentCells(response: response)
        }
    }
}
