import UIKit

class FormWorker{
    var api = API()
    
    func fetchCells(completionHandler: @escaping ([Cell]) -> Void){
        api.fetchCells{cells in
            DispatchQueue.main.async {
                completionHandler(cells)
            }
        }
    }
}
