import UIKit

protocol FundBusinessLogic{
    func fechFunds(request: FundModel.Screen.Request)
}

protocol FundDataStore{
    //var name: String { get set }
}

class FundInteractor: FundBusinessLogic, FundDataStore{
    var presenter: FundPresentationLogic?
    var worker: FundWorker?
    
    func fechFunds(request: FundModel.Screen.Request){
        worker = FundWorker()
        worker?.fetchFunds(onCompletion: { (invests) in
            let response = FundModel.Screen.Response(funds: invests)
            self.presenter?.presentFunds(response: response)
        })
    }
}
