import UIKit

protocol FundPresentationLogic{
    func presentFunds(response: FundModel.Screen.Response)
}

class FundPresenter: FundPresentationLogic{
    weak var viewController: FundDisplayLogic?
    
    func presentFunds(response: FundModel.Screen.Response){
        let viewModel = FundModel.Screen.ViewModel(funds: response.funds)
        viewController?.displayFunds(viewModel: viewModel)
    }
}
