import UIKit

class FundWorker{
    func fetchFunds(onCompletion: @escaping ([Fund]) -> Void){
        ApiManager().fetchFunds { investimentos in
            DispatchQueue.main.async{
                onCompletion(investimentos)
            }
        }
    }
}
