import SafariServices

@objc protocol InvestmentsRoutingLogic {
    func presentMoreInfo(source: InvestmentsViewController, destination: SFSafariViewController)
}

protocol InvestmentsDataPassing {
    var dataStore: InvestmentsDataStore? { get }
}

class InvestmentsRouter: NSObject, InvestmentsRoutingLogic, InvestmentsDataPassing {
    weak var viewController: InvestmentsViewController?
    var dataStore: InvestmentsDataStore?
    
    // MARK: Routing
    
    func presentMoreInfo(source: InvestmentsViewController, destination: SFSafariViewController) {
        source.present(destination, animated: true, completion: nil)
    }
    
}
