@testable import Floating_Mountain
import XCTest

class InvestmentsWorkerTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: InvestmentsWorker!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupInvestmentsWorker()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupInvestmentsWorker() {
        sut = InvestmentsWorker()
    }
    
    // MARK: Tests
    
    func testFetchRequest() {
        // Given
        let expectation = XCTestExpectation(description: "Request to finish")
        let request = Investments.FetchInvestments.Request()
        // When
        sut.fetch(request: request) { _, _ in
            expectation.fulfill()
        }
        // Then
        wait(for: [expectation], timeout: 2.0)
    }
}
