import UIKit

protocol InvestBusinessLogicProtocol{
  func getInvestmentData(request: Invest.InvestInfo.Request)
}

protocol InvestDataStoreProtocol{
  //var name: String { get set }
}

class InvestInteractor: InvestBusinessLogicProtocol, InvestDataStoreProtocol{
  var presenter: InvestPresentationLogicProtocol?
  var worker: InvestWorker?
  
  // MARK: Do something
  
  func getInvestmentData(request: Invest.InvestInfo.Request){
    worker = InvestWorker()
    worker?.getInvestInfo(completionHandler: { data in
        let response = Invest.InvestInfo.Response(investInfo: data)
        self.presenter?.presentInvestData(response: response)
    })
  }
}
