import UIKit

@objc protocol InvestRoutingLogicProtocol{
  func routeToForm()
}

protocol InvestDataPassingProtocol{
  var dataStore: InvestDataStoreProtocol? { get }
}

class InvestRouter: NSObject, InvestRoutingLogicProtocol, InvestDataPassingProtocol{
  weak var viewController: InvestViewController?
  var dataStore: InvestDataStoreProtocol?
  
  // MARK: Routing
    
    func routeToForm() {
        let destination = FormViewController()
        navigateToForm(source: viewController!, destination: destination)
    }

  // MARK: Navigation
  
  func navigateToForm(source: InvestViewController, destination: FormViewController){
    source.navigationController?.pushViewController(destination, animated: false)
  }
  
  // MARK: Passing data
  
  //func passDataToSomewhere(source: InvestDataStore, destination: inout SomewhereDataStore)
  //{
  //  destination.name = source.name
  //}
}
