@testable import Santander
import XCTest

class ContactWorkerTests: XCTestCase
{
  // MARK: Subject under test
  
  var sut: ContactWorker!
  
  // MARK: Test lifecycle
  
  override func setUp()
  {
    super.setUp()
    setupContactWorker()
  }
  
  override func tearDown()
  {
    super.tearDown()
  }
  
  // MARK: Test setup
  
  func setupContactWorker()
  {
    sut = ContactWorker()
  }
    class APIClientSpy: APIClientProtocol
    {
        var fetchCalled = false
        
        func fetchFormCells(completionHandler: @escaping (FormCells?, Error?) -> Void) {
            fetchCalled = true
        }
        
        func fetchFund(completionHandler: @escaping (Fund?, Error?) -> Void) {
            fetchCalled = true
        }
    }
  
  // MARK: Test doubles
  
  // MARK: Tests
  
    func testFetchShouldAskAPIClientToFetchCells()
    {
        // Given
        let api = APIClientSpy()
        sut.apiClient = api
        
        // When
        sut.fetchFormCells { (cells, error) in }
        
        // Then
        XCTAssertTrue(api.fetchCalled, "fetch(completionHandler:) should ask API Client to fetch Cells")
    }
}
