import UIKit

protocol HomeRoutingLogic {
  func routeToInvestment(containerView: UIView)
  func routeToContact(containerView: UIView)
}

protocol HomeDataPassing {
  var dataStore: HomeDataStore? { get }
}

class HomeRouter: NSObject, HomeRoutingLogic, HomeDataPassing {
  
  weak var viewController: HomeViewController?
  var dataStore: HomeDataStore?
  
  // MARK: Routing
  
  func routeToInvestment(containerView: UIView) {
    if let store = dataStore {
      navigateToInvestment(containerView: containerView, store: store)
    }
  }
  
  func routeToContact(containerView: UIView) {
    if let store = dataStore {
      navigateToContact(containerView: containerView, store: store)
    }
  }
  
  // MARK: Navigation
  
  func navigateToInvestment(containerView: UIView, store: HomeDataStore) {
    addController(containerView: containerView, controller: store.investementController)
    removeController(controller: store.contactController)
  }
  
  func navigateToContact(containerView: UIView, store: HomeDataStore) {
    addController(containerView: containerView, controller: store.contactController)
    removeController(controller: store.investementController)
  }
  
  fileprivate func addController(containerView: UIView, controller: UIViewController) {
    containerView.addSubview(controller.view)
    controller.view.frame = containerView.bounds
    controller.view.autoresizingMask = [.flexibleWidth, .flexibleHeight]
    
    if let viewController = viewController {
      controller.didMove(toParentViewController: viewController)
    }
  }
  
  fileprivate func removeController(controller: UIViewController) {
    controller.view.removeFromSuperview()
    controller.removeFromParentViewController()
  }
  
}
