class ContactsInteractor: ContactsBusinessLogic, ContactsDataStore
{
  var presenter: ContactsPresentationLogic?
  var worker: ContactsWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: Contacts.Something.Request)
